import type { Meta, StoryObj } from "@storybook/nextjs";
import { Input } from "./Input";

const meta = {
  component: Input,
  tags: ["autodocs"],
  parameters: {
    layout: "centered",
  },
  argTypes: {
    placeholder: {
      control: "text",
      description: "Placeholder text",
    },
    disabled: {
      control: "boolean",
      description: "Disabled state of the input",
    },
    "aria-invalid": {
      control: "boolean",
      description: "Invalid state of the input",
    },
  },
} satisfies Meta<typeof Input>;

export default meta;
type Story = StoryObj<typeof meta>;

export const Default: Story = {
  render: () => <Input placeholder="ユーザー名で検索" />,
};

export const Disabled: Story = {
  render: () => <Input placeholder="ユーザー名で検索" disabled />,
};

export const Invalid: Story = {
  render: () => <Input placeholder="ユーザー名で検索" aria-invalid="true" />,
};
