import { expect, test } from "@playwright/experimental-ct-react";
import { Input } from "./Input";

test.describe("Input Component", () => {
  test("renders with default props", async ({ mount }) => {
    const component = await mount(<Input placeholder="Enter text" />);
    await expect(component).toBeVisible();
    await expect(component).toHaveRole("textbox");
    await expect(component).toHaveAttribute("placeholder", "Enter text");
  });

  test("handles input events", async ({ mount }) => {
    const component = await mount(<Input placeholder="Type here" />);

    await component.fill("Hello World");
    await expect(component).toHaveValue("Hello World");
  });

  test("shows disabled state correctly", async ({ mount }) => {
    const component = await mount(
      <Input placeholder="Disabled input" disabled />,
    );

    await expect(component).toBeVisible();
    await expect(component).toBeDisabled();
    await expect(component).toHaveAttribute("placeholder", "Disabled input");
  });

  test("shows invalid state correctly", async ({ mount }) => {
    const component = await mount(
      <Input placeholder="Invalid input" aria-invalid="true" />,
    );

    await expect(component).toBeVisible();
    await expect(component).toHaveAttribute("aria-invalid", "true");
    await expect(component).toHaveAttribute("placeholder", "Invalid input");
  });

  test("can be focused and blurred", async ({ mount }) => {
    const component = await mount(<Input placeholder="Focus test" />);

    await component.focus();
    await expect(component).toBeFocused();

    await component.blur();
    await expect(component).not.toBeFocused();
  });
});
