{"output":"import { Avatar as BaseAvatar } from "@base-ui-components/react/avatar";
import clsx from "clsx";
import styles from "./Avatar.module.css";

export type AvatarProps = {
  src?: string;
  alt?: string;
  fallback?: string;
  className?: string;
  size?: "small" | "medium" | "large";
};

export const Avatar = ({
  src,
  alt,
  fallback,
  className,
  size = "medium",
}: AvatarProps) => {
  return (
    <BaseAvatar.Root className={clsx(styles.Root, styles[size], className)}>
      <BaseAvatar.Image className={styles.Image} src={src} alt={alt} />
      <BaseAvatar.Fallback className={styles.Fallback}>
        {fallback}
      </BaseAvatar.Fallback>
    </BaseAvatar.Root>
  );
};
