import { expect, test } from "@playwright/experimental-ct-react";
import { Avatar } from "./Avatar";

test.describe("Avatar", () => {
  test("renders with image", async ({ mount }) => {
    const component = await mount(
      <Avatar
        src="https://via.placeholder.com/150"
        alt="Test Avatar"
        fallback="TA"
      />,
    );

    await expect(component).toBeVisible();
    await expect(component).toContainText("TA");
  });

  test("renders fallback when no image", async ({ mount }) => {
    const component = await mount(<Avatar fallback="山田" />);

    await expect(component).toContainText("山田");
  });

  test("renders with small size", async ({ mount }) => {
    const component = await mount(<Avatar fallback="S" size="small" />);

    await expect(component).toBeVisible();
    await expect(component).toContainText("S");
  });

  test("renders with medium size", async ({ mount }) => {
    const component = await mount(<Avatar fallback="M" size="medium" />);

    await expect(component).toBeVisible();
    await expect(component).toContainText("M");
  });

  test("renders with large size", async ({ mount }) => {
    const component = await mount(<Avatar fallback="L" size="large" />);

    await expect(component).toBeVisible();
    await expect(component).toContainText("L");
  });

  test("applies custom className", async ({ mount }) => {
    const component = await mount(
      <Avatar fallback="C" className="custom-class" />,
    );

    await expect(component).toBeVisible();
